context.global.executeWinkCMD = function (winkName,type,cmd,level) {
var WinkCMDmsg;
node.warn(winkName+' '+type+' '+cmd)
switch (type.toLowerCase()) {

case 'lock':
if (context.global.winkState.locks[winkName])
{
   WinkCMDmsg ={
    "url":"https://winkapi.quirky.com/locks/"+context.global.winkState.locks[winkName].object_id,
    "method": "PUT",
    headers: {
        "Authorization":"Bearer "+context.global.WinkToken,
        "Content-Type":"application/json"
    },
    payload: {
        "desired_state": {
            "locked":(cmd.toLowerCase() =="lock" ? true:false)
        }
    }
}
}
break;
case 'light':
if (context.global.winkState.light_bulbs[winkName]){
 WinkCMDmsg ={
    "url":"https://winkapi.quirky.com/light_bulbs/"+context.global.winkState.light_bulbs[winkName].object_id,
    "method": "PUT",
    headers: {
        "Authorization":"Bearer "+context.global.WinkToken,
        "Content-Type":"application/json"
    },
    payload: {
        "desired_state": {
            "powered":(cmd.toLowerCase() =="on" ? true:false),
            "brightness": level/100
        }
    }
};   
} else if(context.global.winkState.binary_switches[winkName]){
 WinkCMDmsg ={
    "url":"https://winkapi.quirky.com/binary_switches/"+context.global.winkState.binary_switches[winkName].object_id,
    "method": "PUT",
    headers: {
        "Authorization":"Bearer "+context.global.WinkToken,
        "Content-Type":"application/json"
    },
    payload: {
        "desired_state": {
            "powered":(cmd.toLowerCase() =="on" ? true:false)
        }
    }
};
}
break;
case 'group':
if (context.global.winkState.groups[winkName]){
node.warn(context.global.winkState.groups[winkName])
 WinkCMDmsg ={
    "url":"https://winkapi.quirky.com/groups/"+context.global.winkState.groups[winkName].object_id+"/activate",
    "method": "POST",
    headers: {
        "Authorization":"Bearer "+context.global.WinkToken,
        "Content-Type":"application/json"
    },
    payload: {
        "desired_state": {
            "powered":(cmd.toLowerCase() =="on" ? true:false),
            "brightness": level/100
        }
    }
  }
 }
break;
case 'shortcut':
if (context.global.winkState['scenes'].winkName){
 WinkCMDmsg ={
    "url":"https://winkapi.quirky.com/scenes/"+context.global.winkState.scenes[winkName].object_id+"/activate",
    "method": "POST",
    headers: {
        "Authorization":"Bearer "+context.global.WinkToken,
        "Content-Type":"application/json"
    }
  }
 }
break;
default:
WinkCMDmsg={}
}
node.warn(WinkCMDmsg.url)
return WinkCMDmsg;
}

