func:
get_color=function(){
    return context.global.wnr.randomColor({
        hue: context.global.holiday_light_mode,
        luminosity: 'bright'
        }).replace("#","");
}

gen_msg=function(winkName,color){
    if (context.global.HOLIDAY_DEBUG ) {
        node.warn("   Activating bulb: "+ winkName);
        node.warn("   color: "+ color);
    }
    var WinkCMDmsg = context.global.executeWinkCMD(winkName,"light","color",color);
    node.send(WinkCMDmsg);
};

stop_msg=function(winkName){
    if (context.global.HOLIDAY_DEBUG ) {
        node.warn("   Turning off bulb: "+ winkName);
    }
    var WinkCMDmsg = context.global.executeWinkCMD(winkName,"light","off");
    node.send(WinkCMDmsg);
};


function doAndRepeat(delay){
    setTimeout( function(){
        var color=('holiday_mode_group' in context.global && context.global.holiday_mode_group ? get_color() : null);
     if ('holiday_mode_on' in context.global && context.global.holiday_mode_on){        
        if ('holiday_light_bulbs' in context.global) {
            var lights=[];
            if(!Array.isArray(context.global.holiday_light_bulbs)){
                 lights[0]=context.global.holiday_light_bulbs;
            } else{
                 lights=context.global.holiday_light_bulbs;
            }
            for (var i=0;i<lights.length;i++){
                var light=lights[i];
                gen_msg(light,(color===null ? get_color() : color));
            }
            doAndRepeat(1000*context.global.holiday_keep_color);
         } 
        } else{
                if ('holiday_light_bulbs' in context.global) {
                    var olights=[];
                    if(!Array.isArray(context.global.holiday_light_bulbs)){
                         olights[0]=context.global.holiday_light_bulbs;
                    } else{
                         olights=context.global.holiday_light_bulbs;
                                }            
                    context.global.holiday_mode_running = false;   
                    if (context.global.HOLIDAY_DEBUG ) {
                        node.warn("   Holiday mode off, stopping...");
                    }
                    for (var j=0;j<olights.length;j++){
                        var olight=olights[j];
                        stop_msg(olight);
                    } 
                }
            } 
    },delay);
}
if (!('holiday_mode_running' in context.global)) context.global.holiday_mode_running=false;
    if (context.global.HOLIDAY_DEBUG ) {
        node.warn("Holiday Mode: "+context.global.holiday_mode_on);
        node.warn("Holiday Mode running: "+context.global.holiday_mode_running);
        node.warn("Holiday Mode bulbs: "+context.global.holiday_light_bulbs);
        node.warn("Holiday Mode grouping: "+context.global.holiday_mode_group);        
    }
if ('holiday_mode_on' in context.global && context.global.holiday_mode_on && !context.global.holiday_mode_running) {
    context.global.holiday_mode_running=true;
    var effect_delay = 1000*context.global.holiday_keep_color;
    var colorRng=context.global.wnr.randomColor({
        hue: context.global.holiday_light_mode,
        luminosity: 'bright'
        });
        doAndRepeat(0);
}

context.global.HOLIDAY_DEBUG=true;
//context.global.holiday_mode_running=false;
node.warn('Holiday Debug mode on');
return msg;
