var pct = function(value) {
    return ((value > 1.0 ? value : value * 100).toFixed(0) + '%')
}
var dual_temp = function (value) {
    return (typeof value === 'number' ? (((value -32) / 1.8).toFixed(1) + 'C / '+ value.toFixed(1) + 'F') : '')
}

var webIcon = function(icon){
    if(icon == 'clear-day') { return "wi-day-sunny" }
    if(icon == 'clear-night') { return "wi-night-clear" }
    if(icon == 'rain') { return "wi-rain" }
    if(icon == 'snow') { return "wi-snow" }
    if(icon == 'sleet') { return "wi-day-sleet" }
    if(icon == 'wind') { return "wi-day-windy" }
    if(icon == 'fog') { return "wi-fog" }
    if(icon == 'cloudy') { return "wi-cloudy" }
    if(icon == 'partly-cloudy-day') { return "wi-day-cloudy" }
    if(icon == 'partly-cloudy-night') { return "wi-night-cloudy" }
    if(icon == 'hail') { return "wi-hail" }
    if(icon == 'thunderstorm') { return "wi-thunderstorm" }
    if(icon == 'tornado') { return "wi-meteor" }  
}

