var pct = function(value) {
    return ((value > 1 ? value : (value * 100).toFixed(0)) + '%')
}
var dual_temp = function (value) {
    return (typeof value === 'number' ? (value.toFixed(1) + 'C / ' + ((value * 1.8) + 32).toFixed(1) + 'F') : '')
}

var dollars = function (value) {
    return (typeof value === 'number' ? (value / 100).toFixed(2) : '')
}

var getUvIndex = function(val){
    var value;
    value=parseInt(val);
    return value+' '+(value <=2 ? 'Minimal' : value <=4 ? 'Low' : value<=6 ? 'Moderate' : value<=9 ? 'High!' : 'Very High!');
}

var GenValueText = function (property,value){
    text =  { co_detected     : (value ? 'CO DETECTED'     : '')
            , fault           : (value ? 'FAULT DETECTED'  : '')
            , liquid_detected : (value ? 'LEAK DETECTED'   : '')
            , locked          : (value ? 'LOCKED'          : 'UNLOCKED')
            , loudness        : (value ? 'LOUD'            : '')
            , noise           : (value ? 'NOISY'           : '')
            , motion          : (value ? 'MOTION'          : '')
            , opened          : (value ? 'OPEN'            : 'CLOSED')
            , presence        : (value ? 'PRESENCE'        : '')
            , smoke_detected  : (value ? 'SMOKE DETECTED'  : '')
            , tamper_detected : (value ? 'TAMPER DETECTED' : '')
            , vibration       : (value ? 'VIBRATION'       : '')
            , coordinates     : value
            , distanceFromHome: value+' meters'
            , uvindex         : getUvIndex(value)
            , pressure        : value +' mBar'
            , luminance       : value+' cd/m2'
            , illuminance       : value+' lux'
            , battery         : pct(value)
            , brightness      : pct(value)
            , co_severity     : pct(value)
            , humidity        : pct(value)
            , smoke_severity  : pct(value)
            , remaining       : pct(value)
            , temperature     : value
            }[property]
    if (text === '') text = 'OK'
    return text;
}

var sendMsg = function(key,value){
    var newMsg={
  method: 'POST',
  url: 'https://groker.initialstate.com/api/events',
  headers: {
    "Content-Type": "application/json",
    "X-IS-AccessKey": context.global.InitialStateKey,
    "X-IS-BucketKey": context.global.InitialStateBucket,
    "Accept-Version": '~0'
  },
    payload: 
    {
    "key": key,
    "value":value
}
}
node.send(newMsg);
}
 if (context.global.DEBUG){
    node.warn(winkObjectType);
    node.warn(msg.payload.name);
}
var ds=msg.payload.desired_state;
var lr=msg.payload.last_reading;
switch (winkObjectType){
    case 'binary_switches':
        if (!('powered' in ds) || ds.powered===lr.powered){
        keyName=msg.payload.name+' Brightness';
        keyValue=(msg.payload.last_reading.powered ? 100: 0);
        sendMsg(keyName,keyValue);
        }
        break;
    case 'light_bulbs':
        if (!('powered' in ds) || ('powered' in ds && ds.powered==lr.powered && ds.brightness==lr.brightness)){
        keyName=msg.payload.name+' Brightness';
        keyValue=(msg.payload.last_reading.powered ? pct(msg.payload.last_reading.brightness) : 0);
        sendMsg(keyName,keyValue);
        }
        break;
    case 'locks':
        keyName=msg.payload.name+' Status';
        keyValue= GenValueText('locked',msg.payload.last_reading.locked);
        sendMsg(keyName,keyValue);
        break;
    case 'thermostats':
        keyName=msg.payload.name+' Temperature';
        keyValue=msg.payload.last_reading.temperature;
        sendMsg(keyName,keyValue);
        break;
 case 'sensor_pods':
        for (var i=0; i< context.global.winkDevCap[msg.payload.name].fields.length; i++){
        sensor_type=context.global.winkDevCap[msg.payload.name].fields[i].field;
        keyName=msg.payload.name+' '+context.global.winkDevCap[msg.payload.name].fields[i].field.replace("_"," ");
        keyValue=GenValueText(context.global.winkDevCap[msg.payload.name].fields[i].field,context.global.winkState.sensor_pods[msg.payload.name][sensor_type]);
        sendMsg(keyName,keyValue);
        }
        break;
    case 'propane_tanks':
        keyName=msg.payload.name+' Propane';
        keyValue= GenValueText('remaining',msg.payload.last_reading.remaining);
        sendMsg(keyName,keyValue);
        break;

    case 'cameras':
        //node.warn('camera subscription event '+msg.payload.last_reading.motion)
        keyName=msg.payload.name+' Motion';
        keyValue= GenValueText('motion',msg.payload.last_reading.motion);
        sendMsg(keyName,keyValue);
        break;    
}

}
return;

if ('camera_motion' in context.global){
if(context.global.CloudantBkUP && (msg.payload.object_type=='camera' || msg.payload.object_type=='sensor_pod') && ('motion' in msg.payload.last_reading && msg.payload.last_reading.motion===true) && msg.payload.name in context.global.camera_motion)
{
