context.global.MotionOffDelay = function (WinkSensor) {
    var occurences=0;
    var timeout=0;
    if (!context.global.winkState.motionUpdates) return 0;
    for(i = 0; i<context.global.winkState.motionUpdates.length; i++) 
    {                   
        if (context.global.winkState.motionUpdates[i].name==WinkSensor && context.global.winkState.motionUpdates[i].motion) occurences++;
    }
    if (occurences>4) timeout=30*60*1000;
    else if (occurences>2) timeout=10*60*1000;
    else if (occurences>1) timeout= 5*60*1000;
    else timeout=1;
        if (context.global.DEBUG ) node.warn("Sensor: "+WinkSensor+" was active "+ occurences +" times recently, timeout "+timeout);
    return timeout;
};


