};

context.global.isHomeOccupied = function() {
    if (typeof context.global.winkState.groups['.sensors'].motion!=='undefined'){ motionSensors = context.global.winkState.groups['.sensors'].motion.or; } else { motionSensors = false; }
    if (typeof context.global.winkState.groups['.sensors'].occupied!=='undefined'){ occupancySensors = context.global.winkState.groups['.sensors'].occupied.or; } else { occupancySensors = false; }
    return ((motionSensors || occupancySensors || context.global.checkPresence()) ? true : false);
};


context.global.isObject=function(val) {
    if (val === null) { return false;}
    if (val instanceof Array) {return false;}
    return ( (typeof (val) === 'function') || (typeof (val) === 'object') );
};

context.global.cleanWinkState=function(){
    for (var wink_obj in context.global.winkState){
          if (context.global.isObject(context.global.winkState[wink_obj]) && wink_obj.indexOf('Updates')==-1) {
            for (var wink_device in context.global.winkState[wink_obj]) {
               if('eco_system' in context.global.winkState[wink_obj][wink_device] && context.global.winkState[wink_obj][wink_device].eco_system=='WINK'){
                       if (context.global.DEBUG ) node.warn('               resetting object '+wink_device);
                   delete context.global.winkState[wink_obj][wink_device];
               } 
            }
            if (Object.keys(context.global.winkState[wink_obj]).length===0) delete context.global.winkState[wink_obj];
        }
    }
};

context.global.GetCount = function(deviceList,property){
    var true_cnt=0,false_cnt=0;
    for (var dev in deviceList){
        if (deviceList[dev][property]) true_cnt++; 
        else false_cnt++;
    }
    return({'true_cnt':true_cnt,'false_cnt':false_cnt});
}

context.global.GenGroup = function(groupName,property,device){
    var tstmp = new Date();
    if (!('groups' in context.global.winkState)) context.global.winkState.groups={};
    if (!(groupName in context.global.winkState.groups ))
        {
            context.global.winkState.groups[groupName]={
            "eco_system": "WINK",
            "name": groupName,
            "object_type": "groups",
            "object_id": groupName,
            "hidden_at": null,
            "freeboard": 0,
            members:{}
            };
            context.global.winkState.groups[groupName][property]={
                "or": false,
                "and": false,
                "true_count": 0,
                "false_count": 0,
                "updated_at":tstmp,
                "changed_at": tstmp
            };
        }
    context.global.winkState.groups[groupName].members[device.name]=device;
    var counts=context.global.GetCount(context.global.winkState.groups[groupName].members,'opened');
    //context.global.winkState.groups[groupName][property].true_count=(device[property] ? context.global.winkState.groups[groupName][property].true_count+1 : context.global.winkState.groups[groupName][property].true_count-1);
    context.global.winkState.groups[groupName][property].true_count=counts.true_cnt;
     if (context.global.winkState.groups[groupName][property].true_count<0)  context.global.winkState.groups[groupName][property].true_count=0;   
    //context.global.winkState.groups[groupName][property].false_count=(device[property] ? context.global.winkState.groups[groupName][property].false_count-1 : context.global.winkState.groups[groupName][property].false_count+1);
    context.global.winkState.groups[groupName][property].false_count=counts.false_cnt;     
     if ( context.global.winkState.groups[groupName][property].false_count<0)  context.global.winkState.groups[groupName][property].false_count=0;
    context.global.winkState.groups[groupName][property].and=(context.global.winkState.groups[groupName][property].false_count===0 ? true:false);     
    context.global.winkState.groups[groupName][property].or=(context.global.winkState.groups[groupName][property].true_count>0 ? true:false);     
    context.global.winkState.groups[groupName][property].updated_at=new Date();
    context.global.winkState.groups[groupName][property].changed_at=new Date();
}

