context.global.executeTstatCMD = function (winkName,command,Param) {
    var WinkCMDmsg = {};
    if ('new_version' in context.global && context.global.new_version){ 
        var cmd = command.toLowerCase();
        var xParam = (typeof Param === 'string' ? Param.toLowerCase() : Param);
            if (context.global.DEBUG ) node.warn(winkName+' '+cmd+' '+xParam)
        try {
            if (winkName in context.global.winkState.thermostats)
            {
                var devCapbs=context.global.winkDevCap[winkName].fields || [];
                if (context.global.DEBUG ) node.warn(devCapbs);
                WinkCMDmsg ={
                    "url":"https://api.wink.com/thermostats/"+context.global.winkState.thermostats[winkName].object_id,
                    "method": "PUT",
                    headers: {
                        "Authorization":"Bearer "+context.global.WinkToken,
                        "User-Agent":"Manufacturer/Darwin node/0.10.30 Wink/999.99.9",
                        "Content-Type":"application/json"
                    },
                    payload: {
                        desired_state:{}
                    }
                };
                switch (cmd){
                    case 'mode':
                        if (devCapbs[context.global.searchArray(devCapbs,'mode','field')].choices.indexOf(xParam)!=-1 ){
                            WinkCMDmsg.payload.desired_state={
                                "mode":xParam                            
                            }
                        } else WinkCmd={};
                    break;
                    case 'powered':
                        if (xParam=='on' || xParam=='off'){
                            WinkCMDmsg.payload.desired_state={
                                "powered":(xParam=='on' ? true : false)                            
                            }                            
                        } else WinkCmd={};
                    break;
                    case 'heat_start_at':
//                        if (xParam.isNumber){
                            WinkCMDmsg.payload.desired_state={
                                "min_set_point":xParam                            
                            }                            
//                        } else WinkCmd={};
                    break;
                    case 'cool_start_at':
 //                       if (xParam.isNumber){
                            WinkCMDmsg.payload.desired_state={
                                "max_set_point":xParam                            
                            }                            
//                        } else WinkCmd={};
                    break;
                    case 'users_away':
                            if (context.global.searchArray(devCapbs,'users_away','field')!=-1 ){    
                            WinkCMDmsg.payload.desired_state={
                                "users_away":xParam                            
                            }                            
                        } else WinkCmd={};
                    break;            
                    case 'fan':
                            if (context.global.searchArray(devCapbs,'fan_timer_active','field')!=-1 ){    
                            WinkCMDmsg.payload.desired_state={
                                "fan_timer_active":(xParam=='on' ? true : false)                            
                            }                            
                        } else WinkCmd={};
                    break;                     
                    default:
                    WinkCMDmsg={};    
                }
            }
        }
        catch (error){
                if (context.global.DEBUG ) node.warn('execute tStat CMD error: '+ error.message);
        }        
    }
    return WinkCMDmsg;
}

