context.global.executeWinkCMD = function (winkName,type,cmd,level) {
if ('new_version' in context.global && context.global.new_version){    
var WinkCMDmsg;
    if (context.global.DEBUG ) node.warn(winkName+' '+type+' '+cmd)
try {
switch (type.toLowerCase()) {
case 'siren':
if (winkName in context.global.winkState.sirens)
    {
        WinkCMDmsg ={
            "url":"https://api.wink.com/sirens/"+context.global.winkState.sirens[winkName].object_id,
            "method": "PUT",
            headers: {
