    };
 }
break;
case 'group':
if (winkName in context.global.winkState.groups){
    if (context.global.DEBUG ) node.warn(context.global.winkState.groups[winkName]);
 WinkCMDmsg ={
    "url":"https://api.wink.com/groups/"+context.global.winkState.groups[winkName].object_id+"/activate",
    "method": "POST",
    headers: {
        "Authorization":"Bearer "+context.global.WinkToken,
        "Content-Type":"application/json"
    },
    payload: {
        "desired_state": {
            "powered":(cmd.toLowerCase() =="on" ? true:false),
            "brightness": level/100
        }
    }
  };
 }
break;
case 'shortcut':
if (winkName in context.global.winkState.scenes){
 WinkCMDmsg ={
    "url":"https://api.wink.com/scenes/"+context.global.winkState.scenes[winkName].object_id+"/activate",
    "method": "POST",
    headers: {
        "Authorization":"Bearer "+context.global.WinkToken,
        "Content-Type":"application/json"
    }
  };
 }
break;
default:
WinkCMDmsg={url:'not defined'};
}
    if (context.global.DEBUG ) node.warn(WinkCMDmsg.url);
return WinkCMDmsg;
}
catch (error){
        if (context.global.DEBUG ) node.warn('execute WINK CMD error: '+ error.message);
}
} else return;
};

