 context.global.secret_encrypt = function (secret,text){
    var message = (typeof text == 'object' ? JSON.stringify(text) : text);
    var msgBuff = new Buffer(message);
    var nonce = new Buffer(context.global.randomString(24),'utf8');
    var key = new Buffer(32);
    key.fill(0);
    key.write(secret);
        node.warn('message: '+msgBuff);
        node.warn('key: '+key);
        node.warn('nonce: '+nonce);
    try {
        var encMsg = new Buffer(context.global.wnr.crypto_easy(msgBuff,nonce,key,'text'),'ascii');
    } catch (e) {
        node.warn("error encrypting payload");
        return "~ENCRYPT_ERROR~";
    }
        node.warn('encoded message: '+encMsg);
    var finalBuff= new Buffer(nonce.toString() + encMsg.toString());
    return finalBuff.toString('base64');
 }

context.global.secret_decrypt = function (secret,enc_text){
    node.warn('enc_text:' + enc_text);
    var cypherText = new Buffer(enc_text, 'base64');
    var nonce = cypherText.slice(0,24);
    var key = new Buffer(32);
    key.fill(0);
    key.write(secret);
        node.warn('decoding.....')
        node.warn('encoded_str: '+cypherText);
        node.warn('nonce: '+nonce);
        node.warn('key: '+key);
    try {
        var clearText = context.global.wnr.crypto_open_easy(cypherText.slice(24),nonce,key,'text');
    } catch (e) {
        node.warn(e);
        return "~DECRYPT_ERROR~";
    }
    try{
        var clearObj = JSON.parse(clearText);
    } catch (e) {
      return clearText;
    }
    return clearObj;
}

