                }
                // The bulb/switch was already on, we just changed brightness.
                else if (context.global.winkChanged[msg.payload.name].new_state.powered=="On")
                {
                    node.warn(msg.payload.name+" changed brightness from "+context.global.winkChanged[msg.payload.name].old_state.brightness+"% to "+context.global.winkChanged[msg.payload.name].new_state.brightness+"%");
                }
            }
        } */

        // Load the standardized text into an HTTP request to send to the "Robots" tab.
        var newMsg = {
            url:context.global.BlueMixUrlBase+'/red/robots',
            "method": "POST",
            headers: {
                "Authorization":"Bearer "+context.global.FREEBOARD_TOKEN
            },
            payload:{
                'name':msg.payload.name,
                'object_type':msg.payload.object_type,
                'new_state':context.global.winkChanged[msg.payload.name].new_state,
                'old_state':context.global.winkChanged[msg.payload.name].old_state
            }
        };
        return newMsg;
    }
}

if (!('FREEBOARD_TOKEN' in context.global) || context.global.FREEBOARD_TOKEN===''){
    context.global.generate_token('FREEBOARD_TOKEN');
}
return msg;

var lr=msg.payload.last_reading;
//node.warn('Last Reading:');
//node.warn(lr);
var sensorType;
var sensor=context.global.winkState.sensor_pods[msg.payload.name];
if ('motion' in lr) sensorType='motion'
else if ('liquid_detected' in lr) sensorType='leak'
else if ('opened' in lr) sensorType='tripper'
else if (sensor.eco_system == 'BLOOMSKY') sensorType='weather'
else if('lat' in lr ) sensorType='geo'
else sensorType='multi'
var sensorMsg={}
sensorMsg.payload={
        type:'sensor_pods',    
        name:sensor.name,
        object_id:sensor.object_id,
        stype:sensorType,
        last_reading: lr
}
node.send(sensorMsg);

context.global.getWinkState(msg.payload);
delete context.global.winkState._msgid;
return;
