var avgtemp=0;
var avghumidity=0;
var numthermos=0;
var numhumidities=0;
msg.payload={
    "thermostats":context.global.Ecobee.Thermostats,
    "sensor_pods":context.global.Ecobee.Sensors
};
if (typeof context.global.winkState.thermostats=='undefined'){ context.global.winkState.thermostats = new Object(); }
for (var name in msg.payload.thermostats)
{
//    node.warn("Importing: "+name+" thermostat");
    context.global.winkState.thermostats[name]={
        "name":name,
        "object_type": msg.payload.thermostats[name].object_type,
        "object_id": msg.payload.thermostats[name].object_id,
        "freeboard": 0,
        "connection": msg.payload.thermostats[name].connection,
        "min_set_point": (msg.payload.thermostats[name].min_set_point-32)*(5/9),
        "max_set_point": (msg.payload.thermostats[name].max_set_point-32)*(5/9),
        "mode": msg.payload.thermostats[name].mode,
        "temperature": (msg.payload.thermostats[name].temperature-32)*(5/9),
        "humidity": msg.payload.thermostats[name].humidity,
        "users_away":false
    };
    if (msg.payload.thermostats[name].temperature){ avgtemp+=(msg.payload.thermostats[name].temperature-32)*(5/9); numthermos++; }
    if (msg.payload.thermostats[name].humidity){ avghumidity+=(msg.payload.thermostats[name].humidity); numhumidities++; }
}

avgtemp=avgtemp/numthermos;
avghumidity=avghumidity/numhumidities;

if (typeof context.global.winkState.groups[".sensors"].temperature=='undefined'){ context.global.winkState.groups[".sensors"].temperature = new Object(); }
if (typeof context.global.winkState.groups[".sensors"].humidity=='undefined'){ context.global.winkState.groups[".sensors"].humidity = new Object(); }
context.global.winkState.groups[".sensors"].temperature={
    average:avgtemp
};
context.global.winkState.groups[".sensors"].humidity={
