var max=parseInt(msg.payload.max) || 0; //0-100
var min=parseInt(msg.payload.min) || 0;  //0-100
var period=parseInt(msg.payload.period) ||0; //in secondsdd
var delay=parseInt(msg.payload.step_time) || (period >120 ? 20:10); //in seconds
var timeout_adjust=0;
gen_msg=function(winkName,type,level,tstmp){
    var WinkCMDmsg = context.global.executeWinkCMD(winkName,type,(level > 0 ? 'on' : 'off'),level);
    node.send(WinkCMDmsg);
    delete context.global.timedOutCmd[tstmp];
};
function doWithTimeout(i,delay){
    var ind=Date.now();
    if (!('timedOutCmd' in context.global)) context.global.timedOutCmd={};
    var timeoutId=setTimeout(function(){gen_msg(msg.payload.winkName,msg.payload.type,i,ind);},delay);
    if (context.global.DEBUG) node.warn(timeoutId);
    context.global.timedOutCmd[ind]={
        "object":msg.payload.winkName,
        "type":msg.payload.type,
        "timeOutId":timeoutId
    };
}
if (max>min && ((msg.payload.type=='light' && msg.payload.winkName in context.global.winkState.light_bulbs) || msg.payload.type=='group')){
    var step=Math.round((max-min)/period*delay)-1;
    if (step<1) step=1;
    var i=1;
    var curr_br=min;
    var effect_delay=Math.round(period/((max-min)/step)*1000);
    if (context.global.DEBUG) node.warn('step:'+step+' effect_delay:'+effect_delay);
    if (curr_br>0)  doWithTimeout(curr_br,0);
    do {
    	timeout_adjust++;
    	curr_br=min+i;
        doWithTimeout(curr_br,timeout_adjust*effect_delay);
        i+=step;
    } while(min+i<max);
    timeout_adjust++;
    if (curr_br<max) doWithTimeout(max,timeout_adjust*effect_delay);
}
return;

