setLockMode=function(lock,mode){
if (lock in context.global.winkState.locks && checkDevCap(context.global.winkDevCap[lock].fields , mode))
{
   WinkCMDmsg ={
    "url":"https://winkapi.quirky.com/locks/"+context.global.winkState.locks[lock].object_id,
    "method": "PUT",
    headers: {
        "Authorization":"Bearer "+context.global.WinkToken,
        "Content-Type":"application/json"
    },
    payload: {
        "desired_state": {
            "locked": true
        }
    }
