function roundUsing(func, number, prec) {
    var tempnumber = number * Math.pow(10, prec);
    tempnumber = func(tempnumber);
    return tempnumber / Math.pow(10, prec);
}




// Converts numeric degrees to radians
function toRad(Value) {
    return Value * Math.PI / 180;
}


function calcCrow(lat1, lon1, lat2, lon2) {
  var R = 6371; // km
  var dLat = toRad(lat2 - lat1);
  var dLon = toRad(lon2 - lon1);
  Rlat1 = toRad(lat1);
  Rlat2 = toRad(lat2);

  var a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
    Math.sin(dLon/2) * Math.sin(dLon/2) * Math.cos(Rlat1) * Math.cos(Rlat2); 
  var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)); 
  var d = R * c;
  return d;
}

if (context.global.OwnTracksPwd == msg.req.params.key) {
    context.global.OwnTracksMaxAcc = context.global.OwnTracksMaxAcc || 100;
    context.global.OwnTracksGeoRadius = context.global.OwnTracksGeoRadius || 100;    
    if('_type' in msg.payload) {
        if (msg.payload._type === 'encrypted') {
            var secret = context.global.OwnTracksPwd;
            var cypherText = new Buffer(msg.payload.data, 'base64');
            var nonce = cypherText.slice(0,24);
            var key = new Buffer(32);
            key.fill(0);
            key.write(secret);
            try {
                var clearText = context.global.wnr.crypto_open_easy(cypherText.slice(24),nonce,key,"text");
            } catch (e) {
                node.warn("error decrypting payload");
                return;
            }
            try{
                var clearObj = JSON.parse(clearText);
            } catch (e) {
                node.warn("decrypted message not JSON");
                return;
            }
            msg.payload = clearObj; 
        }
        var waypoint,user,pos;
        var dst_from_home=-1;    
        var do_send=false;
        if (msg.payload._type == 'transition') {
            waypoint = true;
            do_send = true;
            user = msg.req.params.user;
            pos = msg.payload;
        }
        if (msg.payload._type == 'location') {
        waypoint = false;
        do_send = true;
        user = msg.req.params.user;
        pos = msg.payload;
        if (context.global.OwnTracksIgnorePing && 't' in pos && pos.t==='p') do_send = false;
        }
        if (do_send && pos.acc <= context.global.OwnTracksMaxAcc) {
            pos.lat = roundUsing(Math.ceil, pos.lat, 6);
            pos.lon = roundUsing(Math.ceil, pos.lon, 6);
            if ('batt' in pos ) { 
                pos.batt = (pos.batt !== null ? pos.batt / 100 : 1);
            } else { 
                pos.batt=1;
            }
            if (!('Presence' in context.global)) context.global.Presence={};
            if (!(user in context.global.Presence)) context.global.Presence[user]={"home":"no"};
            var event_trigger='none';
            if ('t' in pos) event_trigger=pos.t;
            if ('trigger' in pos) event_trigger=pos.trigger;
                if ('HomeLocation' in context.global){ 
                    dst_from_home=Math.round(calcCrow(pos.lat,pos.lon,context.global.HomeLocation.lat,context.global.HomeLocation.lon)*1000);
                }        
            loc_msg={
                url:context.global.BlueMixUrlBase+'/red/wink/subscribtions',
                method:"POST",
                payload: {
                     capabilities: { fields: [ 
                      { field: 'lat', type: 'float' },
                      { field: 'lon', type: 'float' },
                      { field: 'battery', type: 'percentage' },
                      { field: 'lastWaypoint', type: 'string' },
                      { field: 'lastEvent', type: 'string' },
                      { field: 'accuracy', type: 'float' },
                      { field: 'distanceFromHome', type: 'float' },
                      { field: 'coordinates', type: 'string' },                      
                      { field: 'trigger', type: 'string' },
                      { field: 'lastUpdated', type: 'timestamp'}
                      ] },
                  device_manufacturer: 'OwnTracks',
                  "eco_system" : "NODE-RED",
                  last_event: 
                   { brightness_occurred_at: null,
                     loudness_occurred_at: null,
                     vibration_occurred_at: null },
                  last_reading: 
                   { 
                        agent_session_id: 'FALSE',
                        connection: true,
                        'lat': pos.lat,
                        'lon': pos.lon,
                        'coordinates':  pos.lat+','+pos.lon,
                        'accuracy' : pos.acc,
                        'trigger'  : event_trigger,
                        'lastUpdated' : pos.tst
                   },
                  manufacturer_device_model: 'owntracks_geolocation_sensor',
                  model_name: 'Geolocation Sensor',
                  name:user+'_Geo',
                  object_id:  user+'_Geo',
                  object_type: 'sensor_pod',
                  radio_type: 'wifi',
                  sensor_pod_id: user+'_Geo',
                  triggers: [],
                  units: {},
                  upc_code: user+'_Geo',
                  upc_id: user+'_Geo'
                  }
            };
            if (pos.batt !== null) loc_msg.payload.last_reading.battery = pos.batt;
            pos.acc=parseInt(pos.acc);
            context.global.OwnTracksGeoRadius = parseInt(context.global.OwnTracksGeoRadius);
            if(dst_from_home!=-1) dst_from_home=loc_msg.payload.last_reading.distanceFromHome=(dst_from_home < pos.acc ? 0 : dst_from_home - pos.acc);   

            if (context.global.GEO_DEBUG) node.warn("user: " + user);
            if (context.global.GEO_DEBUG) node.warn("distance from home:" + dst_from_home);
            if (context.global.GEO_DEBUG) node.warn("message type:" + (waypoint ? 'waypoint' : 'location'));
            if (context.global.GEO_DEBUG) node.warn("WNR home geo radius:"+ context.global.OwnTracksGeoRadius || 100);
            if (context.global.GEO_DEBUG) node.warn("current user state: "+context.global.Presence[user].home);

            if (waypoint /*|| dst_from_home<=50 || (dst_from_home < pos.acc && pos.acc <100)*/) { 
                if (context.global.GEO_DEBUG) node.warn("waypoint branch");
        //            pos.desc=(dst_from_home < pos.acc  ? 'home' : pos.desc);
        //            pos.event=(dst_from_home < pos.acc  ? 'enter' : pos.event);
        //            loc_msg.payload.last_reading.lastWaypoint=(dst_from_home < pos.acc  ? 'home' : pos.desc);
        //            loc_msg.payload.last_reading.lastEvent=(dst_from_home < pos.acc  ? 'enter' : pos.event);
                if (pos.desc.toLowerCase().indexOf('home')!=-1 && context.global.Presence[user].home=="yes") {
                    waypoint=false
                }
                else 
                {
                    loc_msg.payload.last_reading.lastWaypoint=(pos.desc);
                    loc_msg.payload.last_reading.lastEvent=(pos.event);            
                }
            } else
            {
                if (context.global.GEO_DEBUG) node.warn("location branch");
                if(dst_from_home > context.global.OwnTracksGeoRadius && context.global.Presence[user].home=="yes"){
                    if (context.global.GEO_DEBUG) node.warn(user+ " left");
                    waypoint=true;
                    pos.desc='home';
                    pos.event='leave';
                loc_msg.payload.last_reading.lastWaypoint=(pos.desc);                    
                    loc_msg.payload.last_reading.lastEvent='leave';
                } else if (dst_from_home < context.global.OwnTracksGeoRadius && context.global.Presence[user].home==='no') {
                    if (context.global.GEO_DEBUG) node.warn(user+" arrived");                    
                    waypoint=true;
                    pos.desc='home';
                    pos.event='enter';
                    loc_msg.payload.last_reading.lastWaypoint=(pos.desc);
                    loc_msg.payload.last_reading.lastEvent='enter';    
                }
            }
            node.send(loc_msg); 
            if ((waypoint  && pos.desc.toLowerCase().indexOf('home')!=-1) /*|| dst_from_home < pos.acc*/){
                loc_msg1={
                    url:context.global.BlueMixUrlBase+'/red/presence',
                method:"POST",
                payload: {
                "iftttkey":context.global.FREEBOARD_TOKEN,"name":user,"home":(pos.event=='enter' ? "yes" : "no")
                }
                };
                node.send(loc_msg1);
            }
        }
    }
} else {
    node.warn("bad owntracks message");
}

