function setDeviceCapabilities(device,capabilities){
    var capbExists=false;
    var newCapb={};
    newCapb.fields=[];
    if (device in context.global.winkDevCap &&  'fields' in context.global.winkDevCap[device]) {
        for (var i=0;i<context.global.winkDevCap[device].fields.length;i++ ) {
                r=context.global.winkDevCap[device].fields[i];
                newCapb.fields.push(r);
        }
    }
    for (var j=0;j<capabilities.length; j++) {
        c = capabilities[j];
        capbExists=false;
            for (var k=0;k<newCapb.fields.length;k++ ) {
                r=newCapb.fields[k];
                if (r.field == c.capb){
                    capbExists=true;
                    break;
                }
            }
        if (!capbExists) newCapb.fields.push({
              field:c.capb, type: c.type  
        });
    }
    return newCapb;
}


