              }
        };
}

function setSwitchStMsg(Sdevice) {
    var devCapb=[];
    if ("attributes" in Sdevice && 'level' in Sdevice.attributes) {
        devType='light_bulb';
        devCapb = setDeviceCapabilities(Sdevice.component.replace('+',' '),[{capb:'powered', type:'boolean'},{capb:'brightness', "type": 'percentage'}]);
    } else {
        devType='binary_switch';
        devCapb = setDeviceCapabilities(Sdevice.component.replace('+',' '),[{capb:'powered', type:'boolean'}]);        
    }

 wDevice = {
            url:context.global.BlueMixUrlBase+'/red/wink/subscribtions',
            method:"POST",
            payload: {
              capabilities: devCapb,
              device_manufacturer: 'SmartThings',
              "eco_system" : "SmartThings",
              desired_state:{},
              last_reading: 
               { 
                    connection: true,
                    'powered': Sdevice.attributes.switch=='on' ? true : false,
                    'lastUpdated' : new Date().getTime()
               },
              manufacturer_device_model: 'smartthings_'+Sdevice.stream+'_switch',
              model_name:'Smartthings '+devType, 
              name:Sdevice.component.replace('+',' '),
              object_id:Sdevice.devid,
              object_type: devType,
              radio_type: 'wifi',
              sensor_pod_id:Sdevice.devid,
              triggers: [],
              units: {},
              upc_code:Sdevice.component.replace('+',' '),
              upc_id: Sdevice.devid
              }
        };
    if (devType=='light_bulb') wDevice.payload.last_reading.brightness=Sdevice.attributes.level/100;        
    return wDevice;
}


